import React from 'react';
import axios from 'axios';
import Select from 'react-select';

class ProgressUpdater extends React.Component{
	constructor(props){
		super(props)
		this.state={
	      status: "todo",
	      message: ""
	    }
	}

	componentDidMount() {
		if(this.props.user) {
			this.getTopicStatus();
		}
	}

	componentDidUpdate(prevProps) {
		if(this.props.user && !prevProps.user) {
	    	this.getTopicStatus();
		}
	}


	getTopicStatus() {
    	axios.get("http://localhost:5001/just-for-java/us-central1/app/api/get-topic-status", { 
			params: {
      			id: this.props.user.uid,
				topic: this.props.page
    		}
		})
	      .then(res => {
	        console.log(res.data);
	        if(!res.data.status) {
	        	this.setState({
	        		status: "todo",
	        		message: ""
	        	})
	        } else {
	        	this.setState({
	        		status: res.data.status,
	        		message: ""
	        	})
	        }
	      })
	}

	handleChange = (selectedOption) => {
    	console.log("Option selected:", selectedOption.value); //this prints the selected option
    	this.setState({
	      status: selectedOption.value,
	      message: ""
	    }, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.status
			})
		      .then(res => {
		        console.log(res.data);
		        this.setState({
		        	message: "progress saved!"
		        })
		      })
	    })
  	}

	updateTopicStatus(){
		var status = document.getElementById("myProgressUpdater").value;
		this.setState({
	      status: status,
	      message: ""
	    }, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.status
			})
		      .then(res => {
		        console.log(res.data);
		        this.setState({
		        	message: "progress saved!"
		        })
		      })
	    })
	}

	render(){
		const options = [
		  { label: "todo", value: "todo" },
		  { label: "in progress", value: "in progress" },
		  { label: "complete", value: "complete" }
		];
		return (
			<div className="progress">
				{this.props.user ? 
					<div>
						<Select id="mySelect" options={options} onChange={() => this.updateTopicStatus()} /> 
						<select id="myProgressUpdater" className="dropdown" onChange={this.handleChange} value={this.state.status}>
						  <option value="todo">todo</option>
						  <option value="in progress">in progress</option>
						  <option value="complete">complete</option>
						</select>
						{this.state.message}
					</div>
					: "sign in to save your progress!"
				}
			</div>
		)
	}

}

export default ProgressUpdater;
