import React from 'react';
import axios from 'axios';

class ProgressUpdater extends React.Component{
	constructor(props){
		super(props)
	}

	render(){
		console.log(this.props.user)
		return (
			<div>
				{this.props.user ? 
					<div>
						<select id="myProgressUpdater" onchange="updateTopicStatus()">
						  <option value="todo">todo</option>
						  <option value="inProgress">in progress</option>
						  <option value="complete">complete</option>
						</select>
						<button onClick={() => this.updateTopicStatus()}>mark as todo</button> 
						<button onClick={() => this.updateTopicStatus()}>mark as in progress</button> 
						<button onClick={() => this.updateTopicStatus()}>mark as completed</button> 
					</div>
					: "sign in to save your progress!"}
			</div>
		)
	}

	updateTopicStatus(){
		var status = document.getElementById("myProgressUpdater").value;
		console.log(status);
		axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
			id: this.props.user.uid,
			topic: this.props.page,
			status: status
		})
	      .then(res => {
	        console.log(res);
	        console.log(res.data);
	      })
	}

}

export default ProgressUpdater;
