import React from 'react';
import axios from 'axios';
import Select from 'react-select';

const options = [
  { label: "todo", value: "todo" },
  { label: "in progress", value: "in progress" },
  { label: "complete", value: "complete" }
];

class ProgressUpdater extends React.Component{
	constructor(props){
		super(props)
		this.state={
	      selectedOption: { label: "todo", value: "todo" },
	      message: ""
	    }
	}

	componentDidMount() {
		if(this.props.user) {
			this.getTopicStatus();
		}
	}

	componentDidUpdate(prevProps) {
		if(this.props.user && !prevProps.user) {
	    	this.getTopicStatus();
		}
	}

	handleChange = selectedOption => {
    	this.setState({ 
    		selectedOption: selectedOption,
    		message: ""
    	}, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.selectedOption.value
			})
		      .then(res => {
		        console.log(res.data);
		        this.setState({
		        	message: "progress saved!"
		        })
		      })
	    })
	};

	getTopicStatus() {
    	axios.get("http://localhost:5001/just-for-java/us-central1/app/api/get-topic-status", { 
			params: {
      			id: this.props.user.uid,
				topic: this.props.page
    		}
		})
	      .then(res => {
	        console.log(res.data);
	        if(!res.data.status) {
	        	this.setState({
	        		selectedOption: { label: "todo", value: "todo" },
	        		message: ""
	        	})
	        } else {
	        	this.setState({
	        		selectedOption: { label: res.data.status, value: res.data.status },
	        		message: ""
	        	})
	        }
	      })
	}

	updateTopicStatus(){
		var status = document.getElementById("myProgressUpdater").value;
		this.setState({
	      selectedOption: status,
	      message: ""
	    }, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.selectedOption
			})
		      .then(res => {
		        console.log(res.data);
		        this.setState({
		        	message: "progress saved!"
		        })
		      })
	    })
	}

	render(){
		console.log(this.state.selectedOption);
		return (
			<div className="progress">
				{this.props.user ? 
					<div>
						<Select
					      value={this.state.selectedOption}
					      onChange={this.handleChange}
					      options={options}
					    />
						<select id="myProgressUpdater" className="dropdown" onChange={this.handleChange} value={this.state.selectedOption}>
						  <option value="todo">todo</option>
						  <option value="in progress">in progress</option>
						  <option value="complete">complete</option>
						</select>
						{this.state.message}
					</div>
					: "sign in to save your progress!"
				}
			</div>
		)
	}

}

export default ProgressUpdater;
