import React from 'react';
import './SideButton.css';
import {
  useLocation,
  Link
} from "react-router-dom";

class SideButton extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			selectedLink: ""
		}
	}

	componentDidUpdate(prevState, prevProps) {
		if(prevProps) {
			console.log("previous path: " + prevProps.currentPathname);
		}
		console.log("current path: " + this.props.currentPathname);
		console.log("my path: " + this.props.myPathname);
		if(prevProps && prevProps.currentPathname === "/"+this.props.myPathname) {
			document.getElementById(this.props.myPathname).classList.remove("sideButtonSelected");
		}
		if(this.props.currentPathname === "/"+this.props.myPathname) {
			document.getElementById(this.props.myPathname).classList.add("sideButtonSelected");	
		}
	}

	render(){
		return (

				<Link className="link" to={"/"+this.props.myPathname}>
					<span className="sideButton" id={this.props.myPathname} style={{"display": "block"}}>
						{this.props.myPathname}
					</span>
				</Link>

		)
	}
}

export default SideButton;
