import React from 'react';
import axios from 'axios';

class ProgressUpdater extends React.Component{
	constructor(props){
		super(props)
		this.state={
	      status: "todo"
	    }
	}

	componentDidMount() {
		if(this.props.user) {
			this.getTopicStatus();
		}
	}

	componentDidUpdate(prevProps) {
	  if(this.props.user.uid !== prevProps.user.uid) {
	    this.fetchData(this.props.userID);
	  }
	}


	getTopicStatus() {
    	axios.get("http://localhost:5001/just-for-java/us-central1/app/api/get-topic-status", { 
			params: {
      			id: this.props.user.uid,
				topic: this.props.page
    		}
		})
	      .then(res => {
	        console.log(res.data);
	        if(!res.data.status) {
	        	this.setState({
	        		status: "todo"
	        	})
	        } else {
	        	this.setState({
	        		status: res.data.status
	        	})
	        }
	      })
	}

	updateTopicStatus(){
		var status = document.getElementById("myProgressUpdater").value;
		this.setState({
	      status: status
	    }, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.status
			})
		      .then(res => {
		        console.log(res.data);
		      })
	    })
	}

	render(){
		return (
			<div>
				{this.props.user ? 
					<div>
						<select id="myProgressUpdater" onChange={() => this.updateTopicStatus()} value={this.state.status}>
						  <option value="todo">todo</option>
						  <option value="in progress">in progress</option>
						  <option value="complete">complete</option>
						</select>
					</div>
					: "sign in to save your progress!"}
			</div>
		)
	}

}

export default ProgressUpdater;
