import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import firebaseConfig from './firebaseConfig';
import React from 'react';
import './App.css';
import SideButton from './components/SideButton';
import axios from 'axios';
import Home from './components/Home';
import About from './components/About';
import ContactUs from './components/ContactUs';
import Variables from './components/Variables';
import Printing from './components/Printing';
import UserInput from './components/UserInput';
import IfStatement from './components/IfStatement';
import BooleanLogic from './components/BooleanLogic';
import ForLoop from './components/ForLoop';
import WhileLoop from './components/WhileLoop';
import Comments from './components/Comments';
import Functions from './components/Functions';
import RandomNumbers from './components/RandomNumbers';
import ASCII from './components/ASCII';
import Break from './components/Break';
import Strings from './components/Strings';
import Mathematics from './components/Mathematics';
import ArrayList from './components/ArrayList';
import HashMap from './components/HashMap';
import Arrays from './components/Array';
import Arithmetic from './components/Arithmetic';
import Nesting from './components/Nesting';
import GettingStarted from './components/GettingStarted';
import TwoDimArrays from './components/TwoDimArrays';
import Casting from './components/Casting';
import Constants from './components/Constants';
import Overloading from './components/Overloading';
import ForEachLoop from './components/ForEachLoop';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
  Link
} from "react-router-dom";

const firebaseApp = firebase.initializeApp(firebaseConfig);
const firebaseAppAuth = firebaseApp.auth();
const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

class App extends React.Component{

  constructor(props) {
    super(props);
    var sideButtonText = ["home", "about", "contact-us", "getting-started", "variables", "comments", "printing", "arithmetic", "strings", "user-input", "math", "if-statement", "boolean-logic", "constants", "for-loop", "while-loop", "nesting", "break", "functions", "overloading", "array", "for-each-loop", "arraylist", "2d-array", "hashmap", "casting", "random-numbers", "ascii"];
    var sideButtonSelected = "home";

    this.state={
      sideButtonText: sideButtonText,
      sideButtonSelected: sideButtonSelected
    }
  }

  render() {
    window.scrollTo(0, 0); //move to content when you create content component!!! this is not working rn
    const {
      user,
      signOut,
      signInWithGoogle,
    } = this.props;
    return (
      <Router>
        <div className="full">
          <div className = "title">
            <h1>
              just for java
            </h1>
            {
                user
                  ? <button className="login" onClick={signOut}>sign out of account</button>
                  : <button className="login" onClick={signInWithGoogle}>sign in with google</button>
              }

          </div>
          <div className = "bottom">
            <div className = "sidebar">
              {this.state.sideButtonText.map((text, i) =>
                <div key={i}>
                  <SideButton currentPathname={window.location.pathname} myPathname={text}/>
                </div>
              )}
            </div>
            <div className = "content"> 
              <h2>{this.state.sideButtonSelected}:</h2>
              <br/>
              <Content />
            </div>
          </div>
        </div>
      </Router>
    );
  }

}

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);
