import React from 'react';
import axios from 'axios';
import Select from 'react-select';

const options = [
  { label: "todo", value: "todo" },
  { label: "in progress", value: "in progress" },
  { label: "complete", value: "complete" }
];

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    radius: '10px',
    border: state.isSelected ? '1px solid #172A3A' : '1px solid #FFA69E',
    color: state.isSelected ? '#172A3A' : '#FFA69E',
    backgroundColor: state.isSelected ? '#FFA69E' : '#172A3A',
  }),
}

class ProgressUpdater extends React.Component{
	constructor(props){
		super(props)
		this.state={
	      selectedOption: { label: "todo", value: "todo" }
	    }
	}

	componentDidMount() {
		if(this.props.user) {
			this.getTopicStatus();
		}
	}

	componentDidUpdate(prevProps) {
		if(this.props.user && !prevProps.user) {
	    	this.getTopicStatus();
		}
	}

	handleChange = selectedOption => {
    	this.setState({ 
    		selectedOption: selectedOption
    	}, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.selectedOption.value
			})
		      .then(res => {
		        console.log(res.data);
		      })
	    })
	};

	getTopicStatus() {
    	axios.get("http://localhost:5001/just-for-java/us-central1/app/api/get-topic-status", { 
			params: {
      			id: this.props.user.uid,
				topic: this.props.page
    		}
		})
	      .then(res => {
	        console.log(res.data);
	        if(!res.data.status) {
	        	this.setState({
	        		selectedOption: { label: "todo", value: "todo" }
	        	})
	        } else {
	        	this.setState({
	        		selectedOption: { label: res.data.status, value: res.data.status }
	        	})
	        }
	      })
	}

	updateTopicStatus(){
		var status = document.getElementById("myProgressUpdater").value;
		this.setState({
	      selectedOption: status
	    }, () => {
	    	axios.post("http://localhost:5001/just-for-java/us-central1/app/api/update-topic-status", { 
				id: this.props.user.uid,
				topic: this.props.page,
				status: this.state.selectedOption
			})
		      .then(res => {
		        console.log(res.data);
		      })
	    })
	}

	render(){
		return (
			<div className="progress">
				{this.props.user ? 
					<div>
						<Select
							styles={customStyles}
					      	value={this.state.selectedOption}
					      	onChange={this.handleChange}
					      	options={options}
					      	isSearchable={false}
					    />
					</div>
					: "sign in to save your progress!"
				}
			</div>
		)
	}

}

export default ProgressUpdater;
